pragma solidity ^0.8.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {ERC721Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";

contract CopyrightNFT is ERC721Upgradeable, AccessControlUpgradeable {
    bytes32 public constant MINTER = keccak256("MINTER");
    using Counters for Counters.Counter;
    using SafeERC20 for IERC20;

    address private _copyrightToken;

    Counters.Counter private idGen;

    mapping(uint256 => string) private tokenToUri;

    mapping(uint256 => uint256) private _holdings;
    mapping(uint256 => bool) private _frozen;

    constructor() {} // solhint-disable-line no-empty-blocks

    function initialize(
        string memory name_,
        string memory symbol_,
        address copyrightToken
    ) external initializer {
        __ERC721_init(name_, symbol_);
        __AccessControl_init();

        _copyrightToken = copyrightToken;
        _setupRole(MINTER, _copyrightToken);
    }

    /**
     * @notice creates a new NFT with amountTokens copyright tokens in holdings
     */
    function mint(
        address to,
        uint256 amountTokens,
        string calldata uri
    ) external onlyRole(MINTER) returns (uint256) {
        require(amountTokens % 1 ether == 0, "Can only hold integer amount of tokens");
        idGen.increment();
        uint256 id = idGen.current();
        tokenToUri[id] = uri;
        _mint(to, id);
        _holdings[id] = amountTokens;
        IERC20(_copyrightToken).safeTransferFrom(to, address(this), amountTokens);
        return id;
    }

    /**
     * @notice freezes the NFT, transferring all copyright tokens out of it to the owner, disabling ability to transfer it.
     */
    function freeze(uint256 id) external {
        require(ownerOf(id) == msg.sender, "Can only freeze when owner");
        require(!_frozen[id], "Already frozen");
        _frozen[id] = true;
        IERC20(_copyrightToken).safeTransfer(msg.sender, _holdings[id]);
    }

    /**
     * @notice unfreezes the NFT by transferring proper amount of copyright tokens from the owner, enabling ability to transfer it.
     */
    function unfreeze(uint256 id) external {
        require(ownerOf(id) == msg.sender, "Can only unfreeze when owner");
        require(_frozen[id], "Not frozen");
        _frozen[id] = false;
        IERC20(_copyrightToken).safeTransferFrom(msg.sender, address(this), _holdings[id]);
    }

    function balanceLocked(uint256 id) external view returns (uint256) {
        return _holdings[id];
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

        return tokenToUri[tokenId];
    }

    function _transfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override {
        require(!_frozen[tokenId], "Frozen");
        super._transfer(from, to, tokenId);
    }

    function _burn(uint256 tokenId) internal virtual override {
        require(!_frozen[tokenId], "Frozen");
        super._burn(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721Upgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
","keccak256":"0xa9ceb7050e57295f8a812047eaf2e5a22c36426f0a18ccf1608ecf952ed8a929","license":"MIT"},"contracts/implementations/CopyrightToken.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./CopyrightNFT.sol";
import "./StakingERC721.sol";

import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";

/**
 * @title the implementation to clone on each pod creation
 * @author Eric Nordelo
 */
contract CopyrightToken is ERC20Upgradeable, PausableUpgradeable, AccessControlUpgradeable {
    bytes32 public constant WITHDRAW_MANAGER_ROLE = keccak256("WITHDRAW_MANAGER_ROLE");
    uint256 private constant PRICE_PRECISION = 10**18;

    address private _copyrightNFT;
    StakingERC721 private _stakingNFT;

    event Combined(address indexed owner, address nft, uint256 indexed nftId, uint256 amount);

    constructor() {} // solhint-disable-line no-empty-blocks

    /**
     * @notice ensures that the pod token contract begins paused and with the default admin
     */
    function initialize(
        string calldata _name,
        string calldata _symbol,
        address copyrightNFT,
        address stakingNFT,
        address manager
    ) external initializer {
        __ERC20_init(_name, _symbol);
        __AccessControl_init();
        __Pausable_init();

        _copyrightNFT = copyrightNFT;
        _stakingNFT = StakingERC721(stakingNFT);

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(DEFAULT_ADMIN_ROLE, manager);
    }

    function combine(uint256 amount, string calldata uri) external {
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");
        uint256 amountToCombine = (amount / 1e18) * 1e18;
        require(amountToCombine > 0, "Insufficient tokens to combine");
        approve(_copyrightNFT, amountToCombine);
        uint256 nftId = CopyrightNFT(_copyrightNFT).mint(msg.sender, amountToCombine, uri);
        emit Combined(msg.sender, _copyrightNFT, nftId, amount);
    }

    function pause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    function mint(address to, uint256 amount) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _mint(to, amount);

        _stakingNFT.writeSupplyCheckpoint(true, amount);
    }

    function burn(address to, uint256 amount) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _burn(to, amount);

        _stakingNFT.writeSupplyCheckpoint(false, amount);
    }

    /**
     * @dev ensures that only minting is allowed when paused
     */
    function transfer(address recipient, uint256 amount) public override whenNotPaused returns (bool) {
        return super.transfer(recipient, amount);
    }

    /**
     * @dev ensures that only minting is allowed when paused
     */
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override whenNotPaused returns (bool) {
        return super.transferFrom(sender, recipient, amount);
    }

    function transferContractBalance(
        address to,
        uint256 amount,
        address tokenAddress
    ) external onlyRole(WITHDRAW_MANAGER_ROLE) {
        uint256 balance = ERC20Upgradeable(tokenAddress).balanceOf(address(this));
        require(balance >= amount, "Insuficient funds");

        require(ERC20Upgradeable(tokenAddress).transfer(to, amount), "Fail to transfer");
    }

    /**
     * @notice allows the return of funds after a pod disposal
     */
    function returnFunds(
        address investor,
        address fundingToken,
        uint256 amount
    ) external onlyRole(WITHDRAW_MANAGER_ROLE) {
        require(ERC20Upgradeable(fundingToken).transfer(investor, amount), "Fail to transfer");
    }
}
","keccak256":"0xa336c2b79465f12d1be08d365c871dd9ca9c52c3803dfef08ccb7b36023a3f38","license":"MIT"},"contracts/implementations/DistributionManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721ReceiverUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/IOpenSalesManager.sol";
import "./StakingERC721.sol";
import "./CopyrightNFT.sol";

import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {StakingGovernance} from "./StakingGovernance/StakingGovernance.sol";

struct Sale {
    address secretWallet;
    address paymentToken;
    uint256 price;
}

contract DistributionManager is IERC721ReceiverUpgradeable, AccessControlUpgradeable {
    using SafeERC20 for IERC20;

    bytes32 public constant STAKING_GOVERNANCE = keccak256("STAKING_GOVERNANCE");

    uint256 public constant DECIMALS = 10**18;

    address public podFundingToken;
    address public copyrightFractionToken;
    uint256 public investorShare;

    uint256 public lastRoyalty;
    uint256 public cumulativeRevenue;
    uint256 public totalCFStaked;
    uint256 public totalPTStaked;
    uint256 public totalSharesCopyright;
    uint256 public totalSharesInvestor;

    address public copyrightFractionStakingNFT;
    address public stakingGovernance;

    address private _copyrightNFT;
    address private _erc721WithRoyalty;

    address public immutable stakingERC721Beacon;
    address public immutable stakingGovernanceBeacon;
    address public immutable openSale;

    mapping(address => uint256) private _positions;
    mapping(uint256 => uint256) private _nftPositions;
    mapping(uint256 => Sale) public onSale;

    event CopyrightStaked(address staker, uint256 amount, uint256 tokenId);
    event CopyrightUnstaked(address destination, uint256 amount, uint256 tokenId);
    event RewardsClaimed(address claimTo, uint256 reward, uint256 tokenId);
    event SaleWithPrivateNetworkApproved(
        uint256 tokenId,
        uint256 price,
        address paymentToken,
        address secretWallet
    );
    event SaleWithPrivateNetworkCompleted(
        uint256 tokenId,
        uint256 price,
        address paymentToken,
        address secretWallet
    );

    constructor(
        address _stakingERC721Beacon,
        address _stakingGovernance,
        address _openSale
    ) {
        stakingERC721Beacon = _stakingERC721Beacon;
        stakingGovernanceBeacon = _stakingGovernance;
        openSale = _openSale;
    }

    function initialize(
        address _podFundingToken,
        address _copyrightFractionToken,
        string memory podName,
        string memory podSymbol,
        uint256 _investorShare,
        address copyrightNFT,
        address erc721WithRoyalty
    ) external initializer returns (address stakingNFT) {
        require(_copyrightFractionToken != address(0), "Invalid copyright fraction token");
        require(_investorShare <= DECIMALS, "Investor share exceeds 100%");

        podFundingToken = _podFundingToken;
        copyrightFractionToken = _copyrightFractionToken;
        _erc721WithRoyalty = erc721WithRoyalty;

        // deploy the beacon proxy
        copyrightFractionStakingNFT = address(new BeaconProxy(stakingERC721Beacon, ""));

        investorShare = _investorShare;
        _copyrightNFT = copyrightNFT;

        // deploy the beacon proxy
        address stakingGovernanceAddress = address(new BeaconProxy(stakingGovernanceBeacon, ""));
        stakingGovernance = stakingGovernanceAddress;

        StakingGovernance(payable(stakingGovernanceAddress)).initialize(copyrightFractionStakingNFT);

        StakingERC721(copyrightFractionStakingNFT).initialize(
            string(abi.encodePacked("cStaking ", podName)),
            string(abi.encodePacked("cS_", podSymbol)),
            _copyrightFractionToken,
            stakingGovernanceAddress
        );

        _setupRole(STAKING_GOVERNANCE, stakingGovernanceAddress);

        return copyrightFractionStakingNFT;
    }

    function stakeCopyrightNFT(uint256 nftId) external {
        CopyrightNFT nft = CopyrightNFT(_copyrightNFT);
        require(nft.ownerOf(nftId) == msg.sender, "Can only stake owned tokens");
        uint256 tokens = nft.balanceLocked(nftId);
        uint256 stakingId = _stakeCopyrightFractions(msg.sender, tokens);
        _nftPositions[stakingId] = nftId;
        nft.safeTransferFrom(msg.sender, address(this), nftId);
        nft.freeze(nftId);
    }

    function stakeCopyrightFractions(uint256 amount) external {
        require(
            IERC20(copyrightFractionToken).balanceOf(msg.sender) >= amount,
            "Insufficient copyright fraction balance"
        );
        _stakeCopyrightFractions(msg.sender, amount);
        IERC20(copyrightFractionToken).safeTransferFrom(msg.sender, address(this), amount);
    }

    function _stakeCopyrightFractions(address to, uint256 amount) internal returns (uint256) {
        _syncStaking();
        totalCFStaked += amount;
        uint256 id = StakingERC721(copyrightFractionStakingNFT).createPosition(
            to,
            amount,
            totalSharesCopyright
        );
        emit CopyrightStaked(to, amount, id);
        return id;
    }

    function _syncStaking() internal {
        uint256 balance = IERC20(podFundingToken).balanceOf(address(this));
        uint256 x = balance - lastRoyalty;
        _syncShares(x);
        cumulativeRevenue += x;
        lastRoyalty = balance;
    }

    function _syncShares(uint256 x) private {
        if (totalCFStaked != 0) {
            totalSharesCopyright += ((x * investorShare) * 10**18) / (totalCFStaked * DECIMALS);
        }
    }

    function unstakeCopyrightNFT(uint256 stakingId) external {
        CopyrightNFT nft = CopyrightNFT(_copyrightNFT);
        uint256 nftId = _nftPositions[stakingId];
        uint256 tokensToUnstake = nft.balanceLocked(nftId);
        _unstakeCopyrightFractions(stakingId, tokensToUnstake);
        IERC20(copyrightFractionToken).approve(_copyrightNFT, tokensToUnstake);
        nft.unfreeze(nftId);
        nft.safeTransferFrom(address(this), msg.sender, nftId);
    }

    function unstakeCopyrightFractions(uint256 id, uint256 amount) external {
        _unstakeCopyrightFractions(id, amount);
        IERC20(copyrightFractionToken).safeTransfer(msg.sender, amount);
    }

    function _unstakeCopyrightFractions(uint256 id, uint256 amount) internal {
        require(
            StakingERC721(copyrightFractionStakingNFT).ownerOf(id) == msg.sender,
            "Cannot unstake on not owned position"
        );
        _syncShares(IERC20(podFundingToken).balanceOf(address(this)) - lastRoyalty);
        uint256 reward = StakingERC721(copyrightFractionStakingNFT).calculateReward(id, totalSharesCopyright);
        uint256 balance = IERC20(podFundingToken).balanceOf(address(this));
        lastRoyalty = balance - reward;
        StakingERC721(copyrightFractionStakingNFT).decreasePosition(
            id,
            msg.sender,
            amount,
            totalSharesCopyright
        );

        totalCFStaked -= amount;
        emit RewardsClaimed(msg.sender, reward, id);
        emit CopyrightUnstaked(msg.sender, amount, id);
        IERC20(podFundingToken).safeTransfer(msg.sender, reward);
    }

    function claimCopyrightFractionRewards(uint256 id) external {
        require(
            StakingERC721(copyrightFractionStakingNFT).ownerOf(id) == msg.sender,
            "Cannot claim rewards on not owned position"
        );
        _syncShares(IERC20(podFundingToken).balanceOf(address(this)) - lastRoyalty);
        _claimRewards(id, copyrightFractionStakingNFT, totalSharesCopyright);
    }

    function _claimRewards(
        uint256 id,
        address nft,
        uint256 totalShares
    ) internal {
        uint256 reward = StakingERC721(nft).calculateReward(id, totalShares);
        lastRoyalty = IERC20(podFundingToken).balanceOf(address(this)) - reward;
        StakingERC721(nft).setPositionTotalShares(id, msg.sender, totalShares);
        emit RewardsClaimed(msg.sender, reward, id);
        IERC20(podFundingToken).safeTransfer(msg.sender, reward);
    }

    function getCopyrightRewards(uint256 id) external view returns (uint256 reward) {
        reward = StakingERC721(copyrightFractionStakingNFT).calculateReward(id, totalSharesCopyright);
    }

    function getCopyrightStakingPositions(address owner) external view returns (uint256[] memory) {
        return StakingERC721(copyrightFractionStakingNFT).getPositions(owner);
    }

    function approveSale(
        uint256 tokenId,
        uint256 price,
        address paymentToken
    ) external onlyRole(STAKING_GOVERNANCE) {
        IERC721Upgradeable(_erc721WithRoyalty).approve(openSale, tokenId);
        IOpenSalesManager(openSale).approveSale(
            _erc721WithRoyalty,
            tokenId,
            paymentToken,
            price,
            address(this),
            address(0)
        );
    }

    function sellWithPrivateNetwork(
        uint256 tokenId,
        uint256 price,
        address paymentToken,
        address secretWallet
    ) external onlyRole(STAKING_GOVERNANCE) {
        IERC721Upgradeable(_erc721WithRoyalty).transferFrom(address(this), secretWallet, tokenId);
        onSale[tokenId] = Sale({paymentToken: paymentToken, price: price, secretWallet: secretWallet});

        emit SaleWithPrivateNetworkApproved(tokenId, price, paymentToken, secretWallet);
    }

    function completeSaleWithPrivateNetwork(uint256 tokenId) external {
        Sale memory sale = onSale[tokenId];
        require(sale.price > 0, "Non-existent sale");
        require(
            IERC721Upgradeable(_erc721WithRoyalty).ownerOf(tokenId) != sale.secretWallet,
            "Sale not completed yet"
        );

        emit SaleWithPrivateNetworkCompleted(tokenId, sale.price, sale.paymentToken, sale.secretWallet);
        delete onSale[tokenId];
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes memory
    ) public virtual override returns (bytes4) {
        return this.onERC721Received.selector;
    }
}
","keccak256":"0x525ce890abf76f5b7243b637da1a99f25d29258c69e49da31562196dd2f1b42a","license":"MIT"},"contracts/implementations/StakingERC721.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "../extensions/ERC20VotesCompliant.sol";

import {ERC721Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";

contract StakingERC721 is ERC721Upgradeable, ERC20VotesCompliant, AccessControlUpgradeable {
    using CountersUpgradeable for CountersUpgradeable.Counter;

    bytes32 public constant DISTRIBUTION_MANAGER = keccak256("DISTRIBUTION_MANAGER");
    bytes32 public constant COPYRIGHT_TOKEN = keccak256("COPYRIGHT_TOKEN");

    struct Position {
        uint256 stake;
        uint256 totalShares;
    }

    string private _proxyName;

    string private _proxySymbol;

    CountersUpgradeable.Counter private idGen;

    mapping(uint256 => Position) private positions;

    mapping(address => uint256[]) private positionOwnership;

    /// @notice the address of the corresponding copyright token
    address public copyrightToken;

    /// @notice the address of the corresponding staking governance
    address public stakingGovernance;

    // solhint-disable-next-line no-empty-blocks
    constructor() {}

    function initialize(
        string memory _name,
        string memory _symbol,
        address _copyrightToken,
        address _stakingGovernance
    ) external initializer {
        copyrightToken = _copyrightToken;
        stakingGovernance = _stakingGovernance;

        __AccessControl_init();
        __ERC721_init(_name, _symbol);

        _setupRole(DISTRIBUTION_MANAGER, msg.sender);
        _setupRole(COPYRIGHT_TOKEN, _copyrightToken);
    }

    function createPosition(
        address to,
        uint256 stakeAmount,
        uint256 totalShares
    ) external onlyRole(DISTRIBUTION_MANAGER) returns (uint256 id) {
        idGen.increment();
        id = idGen.current();

        _mint(to, id);

        _moveVotingPower(address(0), delegates(to), stakeAmount);

        positions[id].stake = stakeAmount;
        positions[id].totalShares = totalShares;

        positionOwnership[to].push(id);
    }

    function getPositions(address owner) external view returns (uint256[] memory) {
        return positionOwnership[owner];
    }

    function getPosition(uint256 id) external view returns (uint256 stake, uint256 totalShares) {
        stake = positions[id].stake;
        totalShares = positions[id].totalShares;
    }

    function decreasePosition(
        uint256 id,
        address owner,
        uint256 amount,
        uint256 totalShares
    ) external onlyRole(DISTRIBUTION_MANAGER) {
        require(ownerOf(id) == owner, "Cannot update position if not owner");
        if (amount == positions[id].stake) {
            _burn(id);

            for (uint256 i = 0; i < positionOwnership[owner].length; i++) {
                uint256[] storage ids = positionOwnership[owner];
                if (ids[i] == id) {
                    positionOwnership[owner][i] = ids[ids.length - 1];
                    delete positionOwnership[owner][ids.length - 1];
                }
            }
            delete positions[id];
        } else {
            positions[id].stake -= amount;
            positions[id].totalShares = totalShares;

            // if balance is not burned no transfer occurs, so the voting power should be moved manually
            _moveVotingPower(delegates(owner), address(0), amount);
        }
    }

    function setPositionTotalShares(
        uint256 id,
        address owner,
        uint256 totalShares
    ) external onlyRole(DISTRIBUTION_MANAGER) {
        require(ownerOf(id) == owner, "Cannot update position if not owner");
        positions[id].totalShares = totalShares;
    }

    function calculateReward(uint256 id, uint256 totalAccumulatedShares)
        external
        view
        returns (uint256 reward)
    {
        reward = ((totalAccumulatedShares - positions[id].totalShares) * positions[id].stake) / 10**18;
    }

    /**
     * @dev Get the address `account` is currently delegating to.
     */
    function delegates(address account) public view override returns (address) {
        return _delegates[account] != address(0) ? _delegates[account] : account;
    }

    function writeSupplyCheckpoint(bool increase, uint256 delta) external onlyRole(COPYRIGHT_TOKEN) {
        _writeCheckpoint(_totalSupplyCheckpoints, increase ? _add : _subtract, delta);
    }

    /**
     * @dev Move voting power when tokens are transferred.
     *
     * Emits a {DelegateVotesChanged} event.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 id
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, id);

        // when minting the amount will be 0, so minting should be treated apart
        if (positions[id].stake > 0) {
            _moveVotingPower(delegates(from), delegates(to), positions[id].stake);
        }
    }

    /**
     * @dev Change delegation for `delegator` to `delegatee`.
     *
     * Emits events {DelegateChanged} and {DelegateVotesChanged}.
     */
    function _delegate(address delegator, address delegatee) internal override {
        address currentDelegate = delegates(delegator);
        uint256 delegatorBalance = balanceOf(delegator);
        _delegates[delegator] = delegatee;

        emit DelegateChanged(delegator, currentDelegate, delegatee);

        _moveVotingPower(currentDelegate, delegatee, delegatorBalance);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721Upgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
