pragma solidity ^0.8.2;

import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "./overrides/GovernorSettingsUpgradeable.sol";
import "./overrides/GovernorVotesUpgradeable.sol";
import "./overrides/GovernorVotesQuorumFractionUpgradeable.sol";
import "./overrides/GovernorCountingSimpleUpgradeable.sol";
import "./overrides/Governor.sol";
import "../DistributionManager.sol";
import "./Structs.sol";

import {CopyrightToken} from "../CopyrightToken.sol";
import {StakingERC721} from "../StakingERC721.sol";

contract StakingGovernance is
    Initializable,
    Governor,
    GovernorSettingsUpgradeable,
    GovernorCountingSimpleUpgradeable,
    GovernorVotesUpgradeable,
    GovernorVotesQuorumFractionUpgradeable,
    OwnableUpgradeable
{
    StakingERC721 private _stakingToken;
    address private _distributionManager;

    // solhint-disable-next-line no-empty-blocks
    constructor() initializer {}

    function initialize(address token_) public initializer {
        __Governor_init("Governance");
        __GovernorSettings_init(
            1, /* 1 block */
            45818, /* 1 week */
            0 /* 0 proposal treshold */
        );
        __GovernorCountingSimple_init();
        __GovernorVotes_init(ERC20VotesUpgradeable(token_));
        __GovernorVotesQuorumFraction_init(51);
        __Ownable_init();

        _stakingToken = StakingERC721(token_);
        _distributionManager = msg.sender;
    }

    function proposalThreshold()
        public
        view
        override(Governor, GovernorSettingsUpgradeable)
        returns (uint256)
    {
        // set the proposal threshold to 1 percent of the total supply
        return CopyrightToken(_stakingToken.copyrightToken()).totalSupply() / 100;
    }

    // The following functions are overrides required by Solidity.

    function votingDelay()
        public
        view
        override(IGovernorUpgradeable, GovernorSettingsUpgradeable)
        returns (uint256)
    {
        return super.votingDelay();
    }

    function votingPeriod()
        public
        pure
        override(IGovernorUpgradeable, GovernorSettingsUpgradeable)
        returns (uint256)
    {
        return 45818;
    }

    function quorum(uint256 blockNumber)
        public
        view
        override(IGovernorUpgradeable, GovernorVotesQuorumFractionUpgradeable)
        returns (uint256)
    {
        return super.quorum(blockNumber);
    }

    function getVotes(address account, uint256 blockNumber)
        public
        view
        override(IGovernorUpgradeable, GovernorVotesUpgradeable)
        returns (uint256)
    {
        return super.getVotes(account, blockNumber);
    }

    function propose(
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        string memory description,
        bytes memory secretWalletData
    ) public virtual override returns (uint256) {
        require(targets.length == 1, "Only one call at a time");
        require(targets[0] == _distributionManager, "Only calls to DistributionManager");

        if (bytes4(calldatas[0]) == DistributionManager.sellWithPrivateNetwork.selector) {
            (bool validSigner, bytes memory calldata_) = _validateSecretWallet(secretWalletData);

            require(validSigner, "Invalid secrets");
            require(keccak256(calldatas[0]) == keccak256(calldata_), "Invalid calldata");
        }

        return super.propose(targets, values, calldatas, description, "");
    }

    function _validateSecretWallet(bytes memory secretWalletData)
        internal
        pure
        returns (bool valid, bytes memory calldata_)
    {
        SecretWallet memory secret = abi.decode(secretWalletData, (SecretWallet));

        bytes32 walletHash = keccak256(
            abi.encodePacked(
                uint256(uint160(secret.collection)),
                uint256(uint160(secret.paymentToken)),
                uint256(uint160(secret.podAddress)),
                uint256(secret.tokenId),
                uint256(secret.amount),
                uint256(secret.decimalsOfToken),
                uint256(secret.lockTime)
            )
        );
        bytes32 signedMessage = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", walletHash));

        address signer = ECDSAUpgradeable.recover(signedMessage, secret.v, secret.r, secret.s);

        calldata_ = abi.encodeWithSelector(
            DistributionManager.sellWithPrivateNetwork.selector,
            secret.tokenId,
            secret.amount,
            secret.paymentToken,
            secret.walletAddress
        );

        valid = signer == secret.walletAddress;
    }

    /**
     * @dev Overriden version of the {Governor-state} function.
     */
    function state(uint256 proposalId) public view virtual override returns (ProposalState) {
        ProposalState status = super.state(proposalId);

        if (status != ProposalState.Active) {
            return status;
        }

        // get the total supply when the voting started
        uint256 supply = token.getPastTotalSupply(proposalSnapshot(proposalId));

        (, uint256 forVotes) = proposalVotes(proposalId);

        // finish the voting if 51 percent of for votes is reached
        return forVotes >= (supply * 51) / 100 ? ProposalState.Succeeded : ProposalState.Active;
    }
}
