","keccak256":"0x3556179b7bf9a42960031e337ead01753ed9a1baa1b828de176f1ac835d923d7","license":"MIT"},"contracts/SubscriptionReward.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721URIStorageUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Constants.sol";
import "./Structs.sol";

/**
 * @title the rewards collection contract
 * @notice the collection contract for reward in trax NFTs
 */
contract SubscriptionReward is Initializable, ERC721Upgradeable, AccessControlUpgradeable, UUPSUpgradeable {
    using CountersUpgradeable for CountersUpgradeable.Counter;

    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");

    /// @notice the time to get the full reward
    uint256 public constant TOTAL_TIME = 365 days;

    CountersUpgradeable.Counter private _tokenIdCounter;

    /// @notice the address of the subscription contract
    address public subscription;

    /// @notice the privi trax token
    address public traxToken;

    /// @notice the metadata of the token
    mapping(uint256 => RewardMetadata) public tokenMetadata;

    /**
     * @dev emitted when a user claims accrued reward of a token
     * @param tokenId the id of the reward token
     * @param amount the amount claimed
     */
    event AccruedRewardClaimed(uint256 indexed tokenId, uint256 amount);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() initializer {} // solhint-disable-line

    /**
     * @dev set the addresses for integration
     * @param upgrader_ the address allowed to make the upgrades
     * @param subscription_ the address of the subscription contract
     * @param traxToken_ the address of the privi trax token
     */
    function initialize(
        address upgrader_,
        address subscription_,
        address traxToken_
    ) public initializer {
        subscription = subscription_;
        traxToken = traxToken_;

        __ERC721_init("Privi Trax Rewards Collection", "pTRWD");
        __AccessControl_init();
        __UUPSUpgradeable_init();

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(UPGRADER_ROLE, upgrader_);
    }

    /**
     * @dev mint a reward from a new subscription, only callable by subscription contract
     * @param to_ the reward receiver
     * @param amount_ the amount of trax reward
     */
    function reward(address to_, uint256 amount_) external {
        require(msg.sender == subscription, "Only callable by subscription contract");

        // save the metadata
        tokenMetadata[_tokenIdCounter.current()] = RewardMetadata({
            timestamp: block.timestamp, // solhint-disable-line not-rely-on-time
            totalAmount: amount_,
            claimedAmount: 0
        });

        _safeMint(to_, _tokenIdCounter.current());
        _tokenIdCounter.increment();
    }

    /**
     * @notice allows subscribers to claim his rewards
     * @param tokenId_ the id of the reward token to claim from
     */
    function claimAccruedReward(uint256 tokenId_) external {
        require(ownerOf(tokenId_) == msg.sender, "Only owner can claim");

        RewardMetadata memory metadata = tokenMetadata[tokenId_];

        // solhint-disable-next-line not-rely-on-time
        uint256 accruedTimeFraction = ((block.timestamp - metadata.timestamp) * Constants.GENERIC_PRECISION) /
            (TOTAL_TIME);

        // use GENERIC_PRECISION to avoid fractions under 1 being 0 due to solidity lack of floating points numbers
        if (accruedTimeFraction > Constants.GENERIC_PRECISION) {
            accruedTimeFraction = Constants.GENERIC_PRECISION;
        }

        uint256 claimableAmount = ((accruedTimeFraction * metadata.totalAmount) /
            Constants.GENERIC_PRECISION -
            metadata.claimedAmount);

        if (claimableAmount > 0) {
            tokenMetadata[tokenId_].claimedAmount += claimableAmount;

            // transfer the funds
            require(IERC20(traxToken).transfer(msg.sender, claimableAmount), "Fail to transfer");

            emit AccruedRewardClaimed(tokenId_, claimableAmount);
        }
    }

    // The following functions are overrides required by Solidity.
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721Upgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    // solhint-disable-next-line
    function _authorizeUpgrade(address) internal view override onlyRole(UPGRADER_ROLE) {}
}
