pragma solidity ^0.8.0;

import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "./implementations/ERC721Royalty/ERC721WithRoyalty.sol";

import {CopyrightToken} from "./implementations/CopyrightToken.sol";

import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

contract CreationManager is UUPSUpgradeable, OwnableUpgradeable {
    address private immutable copyrightTokenBeacon;
    address private immutable erc721WithRoyaltyBeacon;

    address private podManagerAddress;

    /**
     * @dev emitted when a CopyrightToken is deployed
     * @param contractAddress the address of the deployed proxy contract
     * @param name the name of the token
     * @param symbol the symbol of the token
     * @param copyrightNFT the address of the CopyrightNFT contract associated
     */
    event CopyrightTokenCreated(address contractAddress, string name, string symbol, address copyrightNFT);

    /**
     * @dev emitted when a Erc721WithRoyalty is deployed
     * @param contractAddress the address of the deployed proxy contract
     * @param name the name of the token
     * @param symbol the symbol of the token
     * @param copyrightAddress the address of the CopyrightToken contract associated
     * @param royaltyPercentage the percentage defining the royalties
     */
    event Erc721WithRoyaltyCreated(
        address contractAddress,
        string name,
        string symbol,
        address copyrightAddress,
        uint16 royaltyPercentage
    );

    modifier onlyPodManager() {
        require(msg.sender == podManagerAddress, "Only pod manager");
        _;
    }

    /**
     * @dev the initializer modifier is to avoid someone initializing
     *      the implementation contract after deployment
     */
    constructor(address _copyrightTokenBeacon, address _erc721WithRoyaltyBeacon) initializer {
        copyrightTokenBeacon = _copyrightTokenBeacon;
        erc721WithRoyaltyBeacon = _erc721WithRoyaltyBeacon;
    }

    /**
     * @dev initializes the contract
     * @param podManager_ the address of the PodManager contract
     */
    function initialize(address podManager_) public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();

        podManagerAddress = podManager_;
    }

    function createCopyrightToken(
        string calldata name,
        string calldata symbol,
        address copyrightNFT
    ) external onlyPodManager returns (address) {
        // deploy the beacon proxy
        address copyrightToken = address(new BeaconProxy(copyrightTokenBeacon, ""));

        emit CopyrightTokenCreated(copyrightToken, name, symbol, copyrightNFT);

        return (copyrightToken);
    }

    function createErc721WithRoyalty(
        string calldata name,
        string calldata symbol,
        address copyrightAddress,
        uint16 royaltyPercentage
    ) external onlyPodManager returns (address) {
        // deploy the beacon proxy
        address erc721WithRoyalty = address(new BeaconProxy(erc721WithRoyaltyBeacon, ""));

        emit Erc721WithRoyaltyCreated(erc721WithRoyalty, name, symbol, copyrightAddress, royaltyPercentage);

        return (erc721WithRoyalty);
    }

    // solhint-disable-next-line no-empty-blocks
    function _authorizeUpgrade(address) internal view override onlyOwner {}
}
