pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "./interfaces/IRoyaltyCategories.sol";
import "./ERC721WithRoyaltyCore.sol";

contract ERC721WithRoyalty is
    ERC721WithRoyaltyCore,
    ERC721Upgradeable,
    OwnableUpgradeable,
    IRoyaltyCategories
{
    mapping(uint256 => string) private tokenToUri;

    mapping(uint256 => string) private _proofOfAuthenticity;

    uint256 public tokenCounter;

    /// @dev song id => song data
    mapping(bytes32 => Song) private _songs;

    /// @dev song id => edition type => quantity to mint
    mapping(bytes32 => mapping(uint256 => uint256)) private _editionQuantities;

    /// @dev song id => edition type => quantity minted
    mapping(bytes32 => mapping(uint256 => uint256)) private _editionMinted;

    /// @dev token id => song id
    mapping(uint256 => bytes32) private _tokenSong;

    /// @dev token id => category
    mapping(uint256 => uint256) private _categories;

    bool public internalURIEnabled;

    string private _internalURI;

    /// @dev how many editions to mint in each mintEditions call
    uint256 public mintHowManyEditions;

    event RoyaltyNFT(
        uint256 initialId,
        uint256 amount,
        address owner,
        string uri,
        address royaltyAddress,
        uint16 bps
    );

    event MasterNFT(uint256 tokenId, bytes32 songId);

    /**
     * @dev the initializer modifier is to avoid someone initializing
     *      the implementation contract after deployment
     */
    constructor() initializer {} // solhint-disable-line no-empty-blocks

    function initialize(
        string memory _name,
        string memory _symbol,
        address _defaultRoyaltyReceiver,
        uint16 _defaultRoyaltyPercentage
    ) external override initializer {
        mintHowManyEditions = 20;

        __ERC721_init(_name, _symbol);
        __Ownable_init();

        _setDefaultRoyalty(
            TokenRoyalty({recipient: _defaultRoyaltyReceiver, bps: _defaultRoyaltyPercentage})
        );
    }

    function setInternalURI(string calldata internalURI_) public onlyOwner {
        _internalURI = internalURI_;
    }

    function setTokenRoyalties(TokenRoyaltyConfig[] calldata royaltyConfigs) external override onlyOwner {
        _setTokenRoyalties(royaltyConfigs);
    }

    function setDefaultRoyalty(TokenRoyalty calldata royalty) external override onlyOwner {
        _setDefaultRoyalty(royalty);
    }

    function exists(uint256 tokenId) public view returns (bool) {
        return _exists(tokenId);
    }

    function mintMasterNFT(
        address to,
        SongData calldata songData_,
        RoyaltyData calldata royaltyData_
    ) public override onlyOwner returns (bytes32 songId) {
        uint256 totalEditions;
        uint256 categoryZeroAmount;

        for (uint256 i = 0; i < songData_.quantityPerCategory.length; i++) {
            totalEditions += songData_.quantityPerCategory[i];

            if (i == 0) {
                categoryZeroAmount = songData_.quantityPerCategory[i];
            }
        }

        if (categoryZeroAmount > 0) {
            require(totalEditions == categoryZeroAmount, "Category 0 is set. Others are invalid.");
        }

        uint256 streamingTokenId = _mintEdition(
            to,
            songData_.tokenURIs[5],
            royaltyData_.royaltyRecipient,
            royaltyData_.royaltyPercent,
            royaltyData_.proofOfAuthenticity,
            "", // empty bytes should be passed for master token
            5
        );

        songId = keccak256(abi.encode(address(this), streamingTokenId));

        for (uint256 i = 0; i < songData_.quantityPerCategory.length; i++) {
            _editionQuantities[songId][i] = songData_.quantityPerCategory[i];
        }

        totalEditions += 1;
        _editionQuantities[songId][5] = 1;
        _editionMinted[songId][5] = 1;

        _songs[songId] = Song(
            songData_.isStreaming,
            streamingTokenId,
            totalEditions,
            1,
            0,
            to,
            songData_.tokenURIs,
            royaltyData_.royaltyPercent,
            royaltyData_.royaltyRecipient,
            royaltyData_.proofOfAuthenticity
        );

        tokenCounter += totalEditions;

        emit MasterNFT(streamingTokenId, songId);
    }

    function mintEditions(bytes32 songId) public onlyOwner {
        require(_songs[songId].totalAmount > 0, "Not existent song");
        require(_songs[songId].totalMinted < _songs[songId].totalAmount, "All tokens have been minted");

        address to = _songs[songId].mintTo;
        string[6] memory tokenURIs = _songs[songId].tokenURIs;
        uint16 royalty = _songs[songId].royalty;
        address royaltyRecipient = _songs[songId].royaltyRecipient;
        string memory proofOfAuthenticity_ = _songs[songId].proofOfAuthenticity_;

        uint256 mintedQty;

        while (_songs[songId].totalMinted < _songs[songId].totalAmount) {
            if (mintedQty == mintHowManyEditions) break;

            uint256 currentCategoryId = _songs[songId].mintingCategory;

            if (_editionQuantities[songId][currentCategoryId] == _editionMinted[songId][currentCategoryId]) {
                _songs[songId].mintingCategory += 1;
                continue;
            } else {
                _songs[songId].totalMinted += 1;
            }

            _mintEdition(
                to,
                tokenURIs[currentCategoryId],
                royaltyRecipient,
                royalty,
                proofOfAuthenticity_,
                songId,
                currentCategoryId
            );

            _editionMinted[songId][currentCategoryId] += 1;

            mintedQty += 1;
        }
    }

    function _mintEdition(
        address to,
        string memory uri,
        address royaltyAddress,
        uint16 bps,
        string memory proofOfAuthenticity_,
        bytes32 songId,
        uint256 category
    ) internal returns (uint256 tokenId) {
        tokenId = _mintWithRoyalty(to, uri, royaltyAddress, bps, proofOfAuthenticity_);

        // create new song id for master
        if (songId == "") {
            songId = keccak256(abi.encode(address(this), tokenId));
        }

        _tokenSong[tokenId] = songId;

        _categories[tokenId] = category;
    }

    function mintBatchWithRoyalty(
        address to,
        uint256 amount,
        string calldata uri,
        address royaltyAddress,
        uint16 bps,
        string calldata proofOfAuthenticity_
    ) public onlyOwner {
        uint256 initialId = tokenCounter + 1;

        for (uint256 i = 0; i < amount; i++) {
            _mintWithRoyalty(to, uri, royaltyAddress, bps, proofOfAuthenticity_);
        }

        emit RoyaltyNFT(initialId, amount, to, uri, royaltyAddress, bps);
    }

    function mintWithRoyalty(
        address to,
        string calldata uri,
        address royaltyAddress,
        uint16 bps,
        string calldata proofOfAuthenticity_
    ) public onlyOwner {
        uint256 tokenId = _mintWithRoyalty(to, uri, royaltyAddress, bps, proofOfAuthenticity_);

        emit RoyaltyNFT(tokenId, 1, to, uri, royaltyAddress, bps);
    }

    function _mintWithRoyalty(
        address to,
        string memory uri,
        address royaltyAddress,
        uint16 bps,
        string memory proofOfAuthenticity_
    ) internal returns (uint256) {
        tokenCounter += 1;
        _mint(to, tokenCounter, uri);

        TokenRoyaltyConfig[] memory config = new TokenRoyaltyConfig[](1);
        config[0] = TokenRoyaltyConfig(tokenCounter, royaltyAddress, bps);
        _setTokenRoyalties(config);

        _proofOfAuthenticity[tokenCounter] = proofOfAuthenticity_;

        return tokenCounter;
    }

    function safeMint(address to, string calldata uri) public onlyOwner {
        tokenCounter = tokenCounter + 1;
        _safeMint(to, tokenCounter, uri);
    }

    function safeMint(
        address to,
        string calldata uri,
        bytes memory _data
    ) public onlyOwner {
        tokenCounter = tokenCounter + 1;
        _safeMint(to, tokenCounter, uri, _data);
    }

    function mint(address to, string calldata uri) public onlyOwner {
        tokenCounter = tokenCounter + 1;
        _mint(to, tokenCounter, uri);
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

        if (internalURIEnabled) {
            return _internalURI;
        } else {
            return tokenToUri[tokenId];
        }
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721Upgradeable, ERC721WithRoyaltyCore)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function _mint(
        address to,
        uint256 tokenId,
        string memory uri
    ) private {
        super._mint(to, tokenId);
        tokenToUri[tokenId] = uri;
    }

    function _safeMint(
        address to,
        uint256 tokenId,
        string memory uri // bytes memory _data
    ) private {
        super._safeMint(to, tokenId);
        tokenToUri[tokenId] = uri;
    }

    function _safeMint(
        address to,
        uint256 tokenId,
        string memory uri,
        bytes memory _data
    ) private {
        super._safeMint(to, tokenId, _data);
        tokenToUri[tokenId] = uri;
    }

    function isStreaming(bytes32 songId) public view returns (bool) {
        return _songs[songId].isStreaming;
    }

    function getCategoryFromToken(uint256 tokenId) public view override returns (uint256) {
        return _categories[tokenId];
    }

    function getMasterIdFromSong(bytes32 songId) public view override returns (uint256) {
        return _songs[songId].masterNFT;
    }

    function getMasterIdFromToken(uint256 tokenId) public view override returns (uint256) {
        return _songs[_tokenSong[tokenId]].masterNFT;
    }

    function getSongTotalEditions(bytes32 songId) public view returns (uint256) {
        return _songs[songId].totalAmount;
    }

    function getSongTotalMinted(bytes32 songId) public view returns (uint256) {
        return _songs[songId].totalMinted;
    }

    function getCategoryQuantityFromSong(bytes32 songId_, uint256 categoryNumber_)
        external
        view
        override
        returns (uint256 quantity)
    {
        quantity = _editionQuantities[songId_][categoryNumber_];
    }

    function getCategoryMintedFromSong(bytes32 songId_, uint256 categoryNumber_)
        external
        view
        returns (uint256 minted)
    {
        minted = _editionMinted[songId_][categoryNumber_];
    }

    function isMaster(uint256 tokenId) public view returns (bool) {
        return _songs[_tokenSong[tokenId]].masterNFT == tokenId;
    }

    function proofOfAuthenticity(uint256 id) public view returns (string memory) {
        return _proofOfAuthenticity[id];
    }

    /// @notice how many editions to mint in one call
    function setMintHowManyEditions(uint256 mintHowManyEditions_) public onlyOwner {
        mintHowManyEditions = mintHowManyEditions_;
    }
}
