pragma solidity ^0.8.0;

struct TokenRoyalty {
    address recipient;
    uint16 bps;
}

struct TokenRoyaltyConfig {
    uint256 tokenId;
    address recipient;
    uint16 bps;
}

struct Song {
    bool isStreaming;
    uint256 masterNFT;
    uint256 totalAmount;
    uint256 totalMinted;
    uint256 mintingCategory;
    address mintTo;
    string[6] tokenURIs;
    uint16 royalty;
    address royaltyRecipient;
    string proofOfAuthenticity_;
}
