pragma solidity ^0.8.0;

import "./Structs.sol";

interface IRoyaltyCategories {
    /**
     * @dev allows minting a new master NFT
     * @param to_ the address recieving the token
     * @param songData_  the data of the song see {SongData} struct
     * @param royaltyData_  the data of the royalties see {RoyaltyData} struct
     */
    function mintMasterNFT(
        address to_,
        SongData calldata songData_,
        RoyaltyData calldata royaltyData_
    ) external returns (bytes32 songId);

    /**
     * @dev allows getting the category number from a token id
     * @param tokenId_ the id of the token
     */
    function getCategoryFromToken(uint256 tokenId_) external view returns (uint256 category);

    /**
     * @dev getter for the quantity of tokens belonging to a specific category for a song
     * @param songId_ the id of the song
     * @param categoryNumber_ the number of the category to consult
     */
    function getCategoryQuantityFromSong(bytes32 songId_, uint256 categoryNumber_)
        external
        view
        returns (uint256 quantity);

    /**
     * @dev allows getting the master token id from a song id
     * @param songId_ the id of the song
     */
    function getMasterIdFromSong(bytes32 songId_) external view returns (uint256 masterId);

    /**
     * @dev allows getting the master token id from a token id
     * @param tokenId_ the id of the token
     */
    function getMasterIdFromToken(uint256 tokenId_) external view returns (uint256 masterId);

    /**
     * @dev allows initializing the collection with default royalties set
     * @param name_ the name of the collection
     * @param symbol_ the symbol of the collection
     * @param defaultRoyaltyReceiver_ the default receiver for royalties
     * @param defaultRoyaltyPercentage_ the default royalty percentage
     */
    function initialize(
        string memory name_,
        string memory symbol_,
        address defaultRoyaltyReceiver_,
        uint16 defaultRoyaltyPercentage_
    ) external;
}
