pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "./ERC1155WithRoyaltyCore.sol";

contract ERC1155WithRoyalty is ERC1155WithRoyaltyCore, ERC1155, Ownable {
    mapping(uint256 => string) private tokenToUri;

    uint256 public counter;

    mapping(uint256 => string) private _proofOfAuthenticity;

    event RoyaltyNFT(
        uint256 id,
        address owner,
        string uri,
        address royaltyAddress,
        uint16 bps
    );

    constructor(string memory uri_)
        ERC1155(uri_)
    {}

    function setTokenRoyalties(TokenRoyaltyConfig[] calldata royaltyConfigs)
        external
        override
        onlyOwner
    {
        _setTokenRoyalties(royaltyConfigs);
    }

    function setDefaultRoyalty(TokenRoyalty calldata royalty)
        external
        override
        onlyOwner
    {
        _setDefaultRoyalty(royalty);
    }

    function mint(
        address to,
        uint256 amount,
        string calldata uri
    ) public {
        counter += 1;
        _mint(to, counter, amount, uri);
    }

    function mintWithId(
        address to,
        uint256 tokenId,
        uint256 amount,
        string calldata uri
    ) public {
        _mint(to, tokenId, amount, uri);
    }

    function burn(
        address from,
        uint256 id,
        uint256 amount
    ) public onlyOwner {
        _burn(from, id, amount);
    }

    function burnBatch(
        address from,
        uint256[] memory ids,
        uint256[] memory amounts
    ) public onlyOwner {
        _burnBatch(from, ids, amounts);
    }

    function mintBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        string calldata data
    ) public {
        _mintBatch(to, ids, amounts, '');
        for (uint256 i = 0; i < ids.length; i++) {
            tokenToUri[ids[i]] = data;
        }
    }

    function mintBatchWithoutId(
        address to,
        uint256[] memory amounts,
        string memory data
    ) public {
        counter += 1;
        for (uint256 i = 0; i < amounts.length; i++) {
            _mint(to, counter, amounts[i], '');
            tokenToUri[counter] = data;

        }
    }

    function proofOfAuthenticity(uint256 id) public view returns (string memory) {
        return _proofOfAuthenticity[id];
    }

    function mintWithRoyalty(
        address to,
        uint256 amount,
        string calldata uri,
        address royaltyAddress,
        uint16 bps,
        string calldata proofOfAuthenticity_
    ) public {
        require(amount <= 100, "Amount can't be greater than 10!");

        for (uint256 i = 0; i < amount; i++) {
            counter += 1;
            _mint(to, counter, 1, uri);
            
            TokenRoyaltyConfig[] memory config = new TokenRoyaltyConfig[](1);
            config[0] = TokenRoyaltyConfig(counter, royaltyAddress, bps);
            _setTokenRoyalties(config);

            _proofOfAuthenticity[counter] = proofOfAuthenticity_;

            emit RoyaltyNFT(counter, to, uri, royaltyAddress, bps);
        }
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, ERC1155WithRoyaltyCore)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function _mint(
        address to,
        uint256 tokenId,
        uint256 amount,
        string calldata uri
    ) private {
        super._mint(to, tokenId, amount, '');
        tokenToUri[tokenId] = uri;
    }

    function uri(uint256 tokenId) public view override returns (string memory) {
        return tokenToUri[tokenId];
    }

}
