pragma solidity ^0.8.0;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "./ERC1155WithRoyalty.sol";

contract RoyaltyFactory {
    address[] public allRoyalties;
    uint256 royaltyCounter;

    event LoyaltyERC1155Created(address nftAddress);

    constructor() {
        royaltyCounter = 0;
    }

    function createRoyaltyERC1155(
        string calldata uri,
        uint256 amount,
        address royaltyAddress, 
        uint16 bps, 
        string calldata proofOfAuthenticity
    )
        external
        returns (address loyalty, uint256 counter)
    {
        ERC1155WithRoyalty _loyalty = new ERC1155WithRoyalty(uri);
        _loyalty.transferOwnership(msg.sender);

        loyalty = address(_loyalty);
        allRoyalties.push(loyalty);
       
        _loyalty.mintWithRoyalty(msg.sender, amount, uri, royaltyAddress, bps, proofOfAuthenticity);
        emit LoyaltyERC1155Created(loyalty);

        royaltyCounter++;
        counter = royaltyCounter;
    }
}
