pragma solidity ^0.8.4;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "./governance/ProtocolParameters.sol";
import "./Structs.sol";

/**
 * @title the contract handling the funds and revenue
 */
contract FundsManager is Initializable, AccessControlUpgradeable, UUPSUpgradeable {
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");

    /// @notice the address of the protocol parameters
    ProtocolParameters public protocol;

    /// @notice the address of the revenue oracle
    address public revenueOracle;

    /// @notice provide a getter for the withdrawable funds
    mapping(address => WithdrawableFunds) public withdrawableFundsBySong;

    /**
     * @dev emitted when the revenue comes from the oracle
     * @param songContract the address of the song contract
     * @param revenue the revenue to distribute
     */
    event RevenueDistributed(address songContract, uint256 revenue);

    /**
     * @dev the initializer modifier is to avoid someone initializing
     *      the implementation contract after deployment
     */
    constructor() initializer {} // solhint-disable-line

    /**
     * @dev set the addresses for integration
     * @param revenueOracle_ the address of the revenue oracle contract
     * @param protocol_ the address of the revenue oracle contract
     * @param upgrader_ the address allowed to upgrade the contract (should be the governance)
     */
    function initialize(
        address revenueOracle_,
        address protocol_,
        address upgrader_
    ) external initializer {
        revenueOracle = revenueOracle_;
        protocol = ProtocolParameters(protocol_);

        __AccessControl_init();
        __UUPSUpgradeable_init();

        _setupRole(UPGRADER_ROLE, upgrader_);
    }

    /**
     * @dev distribute the revenue of a song to the right users
     */
    function distributeRevenue(address song_, uint256 revenue_) external {
        require(msg.sender == revenueOracle, "Only callable by the revenue oracle contract");

        if (revenue_ > 0) {
            WithdrawableFunds storage funds = withdrawableFundsBySong[song_];

            // revenue for the artist (artistSongRevenuePercent has 2 decimals)
            uint256 forArtist = (revenue_ * protocol.artistSongRevenuePercent()) / 10000;

            funds.forArtist += forArtist;

            // the rest is for the positions
            funds.forPositions += revenue_ - forArtist;
        }

        emit RevenueDistributed(song_, revenue_);
    }

    // solhint-disable-next-line
    function _authorizeUpgrade(address) internal view override onlyRole(UPGRADER_ROLE) {}
}
