    mapping(address => bool) public whitelistedCollections;

    /**
     * @dev allows governance to whitelist a collection
     * @param collection_ the address of the collection to whitelist
     */
    function whitelistCollection(address collection_) public virtual {
        whitelistedCollections[collection_] = true;
