pragma solidity ^0.8.2;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title parameters controlled by governance
 * @notice the owner of this contract is the timelock controller of the governance feature
 */
contract ProtocolParameters is Ownable {
    /// @notice the percent that the artist receive from the song revenue (NUMERIC VALUE WITH 2 DECIMALS)
    uint128 public artistSongRevenuePercent;

    /// @notice the minimun quantity of hours you can subscribe for
    uint128 public minTimeOfSubscription;

    event ArtistSongRevenuePercentUpdated(uint128 from, uint128 to);
    event MinTimeOfSubscriptionUpdated(uint128 from, uint128 to);

    /**
     * @dev sets the default (initial) values of the parameters
     *      also transfers the ownership to the governance
     */
    constructor(
        address governance_,
        uint128 artistSongRevenuePercent_,
        uint128 minTimeOfSubscription_
    ) {
        require(artistSongRevenuePercent_ > 0, "Artist percent should be greater than 0");
        require(minTimeOfSubscription_ > 1 hours, "Min hours of subscription should be greater than 1 hour");

        artistSongRevenuePercent = artistSongRevenuePercent_;
        minTimeOfSubscription = minTimeOfSubscription_;

        // transfer ownership
        transferOwnership(governance_);
    }

    function setArtistSongRevenuePercent(uint128 artistSongRevenuePercent_) external onlyOwner {
        require(artistSongRevenuePercent_ > 0, "Artist percent should be greater than 0");
        emit ArtistSongRevenuePercentUpdated(artistSongRevenuePercent, artistSongRevenuePercent_);
        artistSongRevenuePercent = artistSongRevenuePercent_;
    }

    function setMinTimeOfSubscription(uint128 minTimeOfSubscription_) external onlyOwner {
        require(minTimeOfSubscription_ > 1 hours, "Min hours of subscription should be greater than 1 hour");
        emit MinTimeOfSubscriptionUpdated(minTimeOfSubscription, minTimeOfSubscription_);
        minTimeOfSubscription = minTimeOfSubscription_;
    }
}
