pragma solidity ^0.8.4;

import "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../libraries/Stringify.sol";
import "../FundsManager.sol";
import "./OracleStructs.sol";

/**
 * @dev the ownership will be transferred after deployment to the router contract
 */
contract RevenueOracle is Initializable, ChainlinkClient, Ownable {
    using Stringify for uint256;
    using Stringify for address;
    using Stringify for string;

    /// @notice the address of the FundsManager contract
    FundsManager public fundsManager;

    /**
     * @dev oracle configuration parameters
     */
    string public token;
    string public apiURL;
    address public chainlinkNode;
    bytes32 public uint256JobId;
    uint256 public nodeFee;
    address public linkToken;

    /// @dev store the requests for callbacks
    mapping(bytes32 => OracleRequest) private _requests;

    /// @dev the whitelisted callers (song contracts)
    mapping(address => bool) private _whitelist;

    constructor(APIOracleInfo memory oracleInfo_) {
        linkToken = oracleInfo_.linkToken;
        chainlinkNode = oracleInfo_.chainlinkNode;
        uint256JobId = oracleInfo_.jobId.toBytes32();
        nodeFee = oracleInfo_.nodeFee;
        apiURL = "https://nft-validator-o24ig.ondigitalocean.app/";

        setChainlinkToken(linkToken);
    }

    function initialize(address fundsManager_) external initializer {
        fundsManager = FundsManager(fundsManager_);
    }

    /**
     * @dev call to get the accumulated revenue
     *      this method can be called only from a song contract
     *
     * @return requestId the id of the request to the Chainlink oracle
     */
    function updateRevenue(address song_) external returns (bytes32 requestId) {
        require(_whitelist[msg.sender], "Invalid caller");

        Chainlink.Request memory request = buildChainlinkRequest(
            uint256JobId,
            address(this),
            this.processResponse.selector
        );

        // set the request params
        Chainlink.add(
            request,
            "get",
            string(abi.encodePacked(apiURL, "getprice?contractAddress=0x", song_.toString()))
        );
        Chainlink.add(request, "path", "price");

        // send the request
        requestId = sendChainlinkRequestTo(chainlinkNode, request, nodeFee);

        _requests[requestId] = OracleRequest({requester: msg.sender});
    }

    /**
     * @dev function to process the oracle response (only callable from oracle)
     * @param requestId_ the id of the request to the Chainlink oracle
     * @param revenue_ the accumulated revenue
     */
    function processResponse(bytes32 requestId_, uint256 revenue_)
        public
        recordChainlinkFulfillment(requestId_)
    {
        address song = _requests[requestId_].requester;

        fundsManager.distributeRevenue(song, revenue_);
    }

    /**
     * @dev whitelist contracts in order to allow calling this contract
     * (only router can whitelist after deploying the proxy, the router contract owns this one)
     * @param caller_ the caller to whitelist
     */
    function whitelistContract(address caller_) external onlyOwner {
        _whitelist[caller_] = true;
    }
}
","keccak256":"0xeca5703f7c5bd9e0dfeb730ed1db5c2d242d340d01c9deb693085fc7275d10f6","license":"MIT"},"contracts/songs/Song.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../oracles/RevenueOracle.sol";
import "../Constants.sol";

import {SongsRouter} from "./SongsRouter.sol";

/**
 * @title the song contract to handle monetization
 */
contract Song is Initializable {
    /// @notice the address of songs router (song contract fabric)
    address public songsRouter;

    /// @notice the address which should receive the artist revenue
    address public artistRevenueAccount;

    /// @notice the timestamp of the block where the last claim was called
    uint64 public lastClaimTimestamp;

    /// @notice the address of nft collection where the song is registered
    address public songOriginalCollection;

    /// @notice the id of the song nft
    uint256 public songOriginalTokenId;

    /**
     * @dev emitted when a claim is requested
     * @param requestId the Chainlink request id
     */
    event ClaimRequested(bytes32 requestId);

    /**
     * @dev the initializer modifier is to avoid someone initializing
     *      the implementation contract after deployment
     */
    constructor() initializer {} // solhint-disable-line

    /**
     * @dev initializes the song (called by songs router manager after deploy)
     * @param songsRouter_ the address of the SongsRouter contract
     * @param artistRevenueAccount_ the address which should receive the artist revenue
     * @param originalCollection_ the address of nft collection where the song is registered
     * @param tokenId_  the id of the song nft
     */
    function initialize(
        address songsRouter_,
        address artistRevenueAccount_,
        address originalCollection_,
        uint256 tokenId_
    ) external initializer {
        songsRouter = songsRouter_;
        artistRevenueAccount = artistRevenueAccount_;
        songOriginalCollection = originalCollection_;
        songOriginalTokenId = tokenId_;
    }

    /**
     * @notice allows to distribute the weekly revenue to the right users
     */
    function claim() external {
        require(
            block.timestamp > lastClaimTimestamp + Constants.CLAIM_INTERVAL, // solhint-disable-line
            "Claim has to be done weekly"
        );

        address oracle = SongsRouter(songsRouter).revenueOracle();

        bytes32 requestId = RevenueOracle(oracle).updateRevenue(address(this));

        emit ClaimRequested(requestId);
    }
}
","keccak256":"0x34ea1a5b26e21c1e93d44e8e7658b0631da944db2bff0f86be2e76e262f0e251","license":"MIT"},"contracts/songs/SongsRouter.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "../extensions/ERC721Whitelist.sol";

import {RevenueOracle} from "../oracles/RevenueOracle.sol";
import {Song} from "./Song.sol";

/**
 * @title the router to register songs (fabric) and manage collections
 */
contract SongsRouter is Initializable, AccessControlUpgradeable, UUPSUpgradeable, ERC721Whitelist {
    // roles for access control
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
    bytes32 public constant WHITELISTER_ROLE = keccak256("WHITELISTER_ROLE");

    /// @dev the address of the beacon contract for the song proxies upgrades
    address private _beacon;

    /// @notice the counter of the songs staked
    uint256 public stakedSongsCounter;

    /// @notice the address of the revenue oracle
    address public revenueOracle;

    /**
     * @notice map to check if a holder has a token staked from an approved collection
     *
     *  COLLECTION_CONTRACT_ADDRESS => NFT_ID => HOLDER_ACCOUNT_ADDRESS OR ZERO_ADDRESS
     */
    mapping(address => mapping(uint256 => address)) public stakedSongs;

    /**
     * @dev emitted when a ser stakes a song
     * @param collection the address of the collection contract
     * @param tokenId the id of the nft
     * @param staker the address of the account staking the song
     * @param artistRevenueAccount the address of the account receiving the artists shares
     * @param songContract the song contract deployed
     */
    event SongStaked(
        address indexed collection,
        uint256 tokenId,
        address staker,
        address artistRevenueAccount,
        address songContract
    );

    /**
     * @dev the initializer modifier is to avoid someone initializing
     *      the implementation contract after deployment
     */
    constructor() initializer {} // solhint-disable-line

    /**
     * @dev initializes the protocol and router addresses,
     *      sets the UPGRADER_ROLE to governance (only governance can upgrade)
     *      and also the WHITELISTER_ROLE to add and remove collections to the governance
     *
     * @param governance_ the address of the goverance contract (timelock actually)
     * @param beacon_ the beacon upgradeable contract of the songs
     */
    function initialize(
        address governance_,
        address beacon_,
        address revenueOracle_
    ) external initializer {
        _beacon = beacon_;
        revenueOracle = revenueOracle_;

        __AccessControl_init();
        __UUPSUpgradeable_init();

        _setupRole(DEFAULT_ADMIN_ROLE, governance_);
        _setupRole(UPGRADER_ROLE, governance_);
        _setupRole(WHITELISTER_ROLE, governance_);
    }

    /**
     * @dev allows governance to whitelist a collection
     * @param collection_ the address of the collection to whitelist
     */
    function whitelistCollection(address collection_) public override onlyRole(WHITELISTER_ROLE) {
        super.whitelistCollection(collection_);
    }

    /**
     * @dev allows governance to blacklist a collection
     * @param collection_ the address of the collection to blacklist
     */
    function blacklistCollection(address collection_) public override onlyRole(WHITELISTER_ROLE) {
        super.blacklistCollection(collection_);
    }

    /**
     * @notice stake an NFT from a whitelisted collection in the contract,
     *         deploying the corresponding Song proxy contract
     *
     * @param collection_ the address of the collection
     * @param tokenId_ the id of the NFT in the collection
     * @param artistRevenueAccount_ the address which should receive the artists funds
     */
    function stake(
        address collection_,
        uint256 tokenId_,
        address artistRevenueAccount_
    ) external {
        require(whitelistedCollections[collection_], "Collection not approved");
        require(stakedSongs[collection_][tokenId_] == address(0), "Song already staked");

        stakedSongs[collection_][tokenId_] = msg.sender;

        // update the counter
        stakedSongsCounter++;

        // get the nft
        IERC721(collection_).transferFrom(msg.sender, address(this), tokenId_);

        // deploy the beacon proxy
        address newSongAddress = address(
            new BeaconProxy(
                _beacon,
                abi.encodeWithSignature(
                    "initialize(address,address,address,uint256)",
                    address(this),
                    artistRevenueAccount_,
                    collection_,
                    tokenId_
                )
            )
        );

        // whitelist the song in the revenue oracle
        RevenueOracle(revenueOracle).whitelistContract(newSongAddress);

        emit SongStaked(collection_, tokenId_, msg.sender, artistRevenueAccount_, newSongAddress);
    }

    // solhint-disable-next-line
    function _authorizeUpgrade(address) internal view override onlyRole(UPGRADER_ROLE) {}
}
