pragma solidity ^0.8.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {ERC721Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";

contract CopyrightNFT is ERC721Upgradeable, AccessControlUpgradeable {
    bytes32 public constant MINTER = keccak256("MINTER");
    using Counters for Counters.Counter;
    using SafeERC20 for IERC20;

    address private _copyrightToken;

    Counters.Counter private idGen;

    mapping(uint256 => string) private tokenToUri;

    mapping(uint256 => uint256) private _holdings;
    mapping(uint256 => bool) private _frozen;

    constructor() {} // solhint-disable-line no-empty-blocks

    function initialize(
        string memory name_,
        string memory symbol_,
        address copyrightToken
    ) external initializer {
        __ERC721_init(name_, symbol_);
        __AccessControl_init();

        _copyrightToken = copyrightToken;
        _setupRole(MINTER, _copyrightToken);
    }

    /**
     * @notice creates a new NFT with amountTokens copyright tokens in holdings
     */
    function mint(
        address to,
        uint256 amountTokens,
        string calldata uri
    ) external onlyRole(MINTER) returns (uint256) {
        require(amountTokens % 1 ether == 0, "Can only hold integer amount of tokens");
        idGen.increment();
        uint256 id = idGen.current();
        tokenToUri[id] = uri;
        _mint(to, id);
        _holdings[id] = amountTokens;
        IERC20(_copyrightToken).safeTransferFrom(to, address(this), amountTokens);
        return id;
    }

    /**
     * @notice freezes the NFT, transferring all copyright tokens out of it to the owner, disabling ability to transfer it.
     */
    function freeze(uint256 id) external {
        require(ownerOf(id) == msg.sender, "Can only freeze when owner");
        require(!_frozen[id], "Already frozen");
        _frozen[id] = true;
        IERC20(_copyrightToken).safeTransfer(msg.sender, _holdings[id]);
    }

    /**
     * @notice unfreezes the NFT by transferring proper amount of copyright tokens from the owner, enabling ability to transfer it.
     */
    function unfreeze(uint256 id) external {
        require(ownerOf(id) == msg.sender, "Can only unfreeze when owner");
        require(_frozen[id], "Not frozen");
        _frozen[id] = false;
        IERC20(_copyrightToken).safeTransferFrom(msg.sender, address(this), _holdings[id]);
    }

    function balanceLocked(uint256 id) external view returns (uint256) {
        return _holdings[id];
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

        return tokenToUri[tokenId];
    }

    function _transfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override {
        require(!_frozen[tokenId], "Frozen");
        super._transfer(from, to, tokenId);
    }

    function _burn(uint256 tokenId) internal virtual override {
        require(!_frozen[tokenId], "Frozen");
        super._burn(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721Upgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
","keccak256":"0xa9ceb7050e57295f8a812047eaf2e5a22c36426f0a18ccf1608ecf952ed8a929","license":"MIT"},"contracts/implementations/CopyrightToken.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./CopyrightNFT.sol";
import "./StakingERC721.sol";

import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";

/**
 * @title the implementation to clone on each pod creation
 * @author Eric Nordelo
 */
contract CopyrightToken is ERC20Upgradeable, PausableUpgradeable, AccessControlUpgradeable {
    bytes32 public constant WITHDRAW_MANAGER_ROLE = keccak256("WITHDRAW_MANAGER_ROLE");
    uint256 private constant PRICE_PRECISION = 10**18;

    address private _copyrightNFT;
    StakingERC721 private _stakingNFT;

    event Combined(address indexed owner, address nft, uint256 indexed nftId, uint256 amount);

    constructor() {} // solhint-disable-line no-empty-blocks

    /**
     * @notice ensures that the pod token contract begins paused and with the default admin
     */
    function initialize(
        string calldata _name,
        string calldata _symbol,
        address copyrightNFT,
        address stakingNFT,
        address manager
    ) external initializer {
        __ERC20_init(_name, _symbol);
        __AccessControl_init();
        __Pausable_init();

        _copyrightNFT = copyrightNFT;
        _stakingNFT = StakingERC721(stakingNFT);

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(DEFAULT_ADMIN_ROLE, manager);
    }

    function combine(uint256 amount, string calldata uri) external {
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");
        uint256 amountToCombine = (amount / 1e18) * 1e18;
        require(amountToCombine > 0, "Insufficient tokens to combine");
        approve(_copyrightNFT, amountToCombine);
        uint256 nftId = CopyrightNFT(_copyrightNFT).mint(msg.sender, amountToCombine, uri);
        emit Combined(msg.sender, _copyrightNFT, nftId, amount);
    }

    function pause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    function mint(address to, uint256 amount) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _mint(to, amount);

        _stakingNFT.writeSupplyCheckpoint(true, amount);
    }

    function burn(address to, uint256 amount) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _burn(to, amount);

        _stakingNFT.writeSupplyCheckpoint(false, amount);
    }

    /**
     * @dev ensures that only minting is allowed when paused
     */
    function transfer(address recipient, uint256 amount) public override whenNotPaused returns (bool) {
        return super.transfer(recipient, amount);
    }

    /**
     * @dev ensures that only minting is allowed when paused
     */
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override whenNotPaused returns (bool) {
        return super.transferFrom(sender, recipient, amount);
    }

    function transferContractBalance(
        address to,
        uint256 amount,
        address tokenAddress
    ) external onlyRole(WITHDRAW_MANAGER_ROLE) {
        uint256 balance = ERC20Upgradeable(tokenAddress).balanceOf(address(this));
        require(balance >= amount, "Insuficient funds");

        require(ERC20Upgradeable(tokenAddress).transfer(to, amount), "Fail to transfer");
    }

    /**
     * @notice allows the return of funds after a pod disposal
     */
    function returnFunds(
        address investor,
        address fundingToken,
        uint256 amount
    ) external onlyRole(WITHDRAW_MANAGER_ROLE) {
        require(ERC20Upgradeable(fundingToken).transfer(investor, amount), "Fail to transfer");
    }
}
","keccak256":"0xa336c2b79465f12d1be08d365c871dd9ca9c52c3803dfef08ccb7b36023a3f38","license":"MIT"},"contracts/implementations/StakingERC721.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "../extensions/ERC20VotesCompliant.sol";

import {ERC721Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";

contract StakingERC721 is ERC721Upgradeable, ERC20VotesCompliant, AccessControlUpgradeable {
    using CountersUpgradeable for CountersUpgradeable.Counter;

    bytes32 public constant DISTRIBUTION_MANAGER = keccak256("DISTRIBUTION_MANAGER");
    bytes32 public constant COPYRIGHT_TOKEN = keccak256("COPYRIGHT_TOKEN");

    struct Position {
        uint256 stake;
        uint256 totalShares;
    }

    string private _proxyName;

    string private _proxySymbol;

    CountersUpgradeable.Counter private idGen;

    mapping(uint256 => Position) private positions;

    mapping(address => uint256[]) private positionOwnership;

    /// @notice the address of the corresponding copyright token
    address public copyrightToken;

    /// @notice the address of the corresponding staking governance
    address public stakingGovernance;

    // solhint-disable-next-line no-empty-blocks
    constructor() {}

    function initialize(
        string memory _name,
        string memory _symbol,
        address _copyrightToken,
        address _stakingGovernance
    ) external initializer {
        copyrightToken = _copyrightToken;
        stakingGovernance = _stakingGovernance;

        __AccessControl_init();
        __ERC721_init(_name, _symbol);

        _setupRole(DISTRIBUTION_MANAGER, msg.sender);
        _setupRole(COPYRIGHT_TOKEN, _copyrightToken);
    }

    function createPosition(
        address to,
        uint256 stakeAmount,
        uint256 totalShares
    ) external onlyRole(DISTRIBUTION_MANAGER) returns (uint256 id) {
        idGen.increment();
        id = idGen.current();

        _mint(to, id);

        _moveVotingPower(address(0), delegates(to), stakeAmount);

        positions[id].stake = stakeAmount;
        positions[id].totalShares = totalShares;

        positionOwnership[to].push(id);
    }

    function getPositions(address owner) external view returns (uint256[] memory) {
        return positionOwnership[owner];
    }

    function getPosition(uint256 id) external view returns (uint256 stake, uint256 totalShares) {
        stake = positions[id].stake;
        totalShares = positions[id].totalShares;
    }

    function decreasePosition(
        uint256 id,
        address owner,
        uint256 amount,
        uint256 totalShares
    ) external onlyRole(DISTRIBUTION_MANAGER) {
        require(ownerOf(id) == owner, "Cannot update position if not owner");
        if (amount == positions[id].stake) {
            _burn(id);

            for (uint256 i = 0; i < positionOwnership[owner].length; i++) {
                uint256[] storage ids = positionOwnership[owner];
                if (ids[i] == id) {
                    positionOwnership[owner][i] = ids[ids.length - 1];
                    delete positionOwnership[owner][ids.length - 1];
                }
            }
            delete positions[id];
        } else {
            positions[id].stake -= amount;
            positions[id].totalShares = totalShares;

            // if balance is not burned no transfer occurs, so the voting power should be moved manually
            _moveVotingPower(delegates(owner), address(0), amount);
        }
    }

    function setPositionTotalShares(
        uint256 id,
        address owner,
        uint256 totalShares
    ) external onlyRole(DISTRIBUTION_MANAGER) {
        require(ownerOf(id) == owner, "Cannot update position if not owner");
        positions[id].totalShares = totalShares;
    }

    function calculateReward(uint256 id, uint256 totalAccumulatedShares)
        external
        view
        returns (uint256 reward)
    {
        reward = ((totalAccumulatedShares - positions[id].totalShares) * positions[id].stake) / 10**18;
    }

    /**
     * @dev Get the address `account` is currently delegating to.
     */
    function delegates(address account) public view override returns (address) {
        return _delegates[account] != address(0) ? _delegates[account] : account;
    }

    function writeSupplyCheckpoint(bool increase, uint256 delta) external onlyRole(COPYRIGHT_TOKEN) {
        _writeCheckpoint(_totalSupplyCheckpoints, increase ? _add : _subtract, delta);
    }

    /**
     * @dev Move voting power when tokens are transferred.
     *
     * Emits a {DelegateVotesChanged} event.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 id
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, id);

        // when minting the amount will be 0, so minting should be treated apart
        if (positions[id].stake > 0) {
            _moveVotingPower(delegates(from), delegates(to), positions[id].stake);
        }
    }

    /**
     * @dev Change delegation for `delegator` to `delegatee`.
     *
     * Emits events {DelegateChanged} and {DelegateVotesChanged}.
     */
    function _delegate(address delegator, address delegatee) internal override {
        address currentDelegate = delegates(delegator);
        uint256 delegatorBalance = balanceOf(delegator);
        _delegates[delegator] = delegatee;

        emit DelegateChanged(delegator, currentDelegate, delegatee);

        _moveVotingPower(currentDelegate, delegatee, delegatorBalance);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721Upgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
