pragma solidity ^0.8.2;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "../extensions/ManagedFunctions.sol";

/**
 * @title parameters controlled by governance
 * @notice the owner of this contract is the timelock controller of the governance feature
 */
contract ProtocolParameters is UUPSUpgradeable, OwnableUpgradeable, ManagedFunctions {
    uint128 public podVotingPeriod;

    mapping(address => bool) public whitelistedAssets;

    event PodVotingPeriodUpdated(uint128 from, uint128 to);
    event AssetWhitelisted(address asset);
    event AssetBlacklisted(address asset);

    /**
     * @dev the initializer modifier is to avoid someone initializing
     *      the implementation contract after deployment
     */
    constructor() initializer {} // solhint-disable-line no-empty-blocks

    /**
     * @dev initializes the contract,
     *      sets the default (initial) values of the parameters
     *      and also transfers the ownership to the governance
     */
    function initialize(uint128 podVotingPeriod_) public initializer {
        require(podVotingPeriod_ > 0, "Invalid pod voting period");

        podVotingPeriod = podVotingPeriod_;

        __Ownable_init();
        __UUPSUpgradeable_init();
    }

    function setPodVotingPeriod(uint128 podVotingPeriod_) external managed {
        require(podVotingPeriod_ > 0, "Invalid pod voting period");

        emit PodVotingPeriodUpdated(podVotingPeriod, podVotingPeriod_);
        podVotingPeriod = podVotingPeriod_;
    }

    function whitelistAsset(address asset_) external managed {
        require(asset_ != address(0), "Asset token address cannot be 0");
        require(!whitelistedAssets[asset_], "Asset already whitelisted");

        emit AssetWhitelisted(asset_);

        whitelistedAssets[asset_] = true;
    }

    function blacklistAsset(address asset_) external managed {
        require(asset_ != address(0), "Asset token address cannot be 0");
        require(whitelistedAssets[asset_], "Asset not whitelisted");

        emit AssetBlacklisted(asset_);

        delete whitelistedAssets[asset_];
    }

    /**
     * @notice getter for managers
     * @dev should be overrided to return a default manager from address 0
     * @param functionSelector_ the function selector
     * @return manager the current manager
     */
    function getManager(bytes4 functionSelector_) public view override returns (address manager) {
        address assigned = _functionManager[functionSelector_];
        return assigned != address(0) ? assigned : owner();
    }

    // solhint-disable-next-line no-empty-blocks
    function _authorizeUpgrade(address) internal view override onlyOwner {}
}
